import React, { Component } from "react";
import "./App.css";
import logo from "./logo.jpg";
class App extends Component {
  state = {
    response: "",
    post: "",
    responseToPost: ""
  };
  // componentDidMount() {
  //   this.callApi()
  //     .then(res => this.setState({ response: res.express }))
  //     .catch(err => console.log(err));
  // }
  componentDidMount() {
    // fetch data from www.lrt.lt json file
    fetch(`https://www.lrt.lt/static/tvprog/tvprog.json`)
      .then(res => res.json())
      .then(json => {
        let liveShow = json.tvprog.items[0].title;
        let liveShowDesc = json.tvprog.items[0].desc;
        let liveShowStart = json.tvprog.items[0].start;
        let liveShowStop = json.tvprog.items[0].stop;
        console.log(liveShow);
        console.log(liveShowDesc);
        console.log(liveShowStart);
        console.log(liveShowStop);
      })
      .catch(err => {
        console.error(err);
      });
  }
  callApi = async () => {
    const response = await fetch("/api/get");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };
  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch("/api/post", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ post: this.state.post })
    });
    const body = await response.text();
    this.setState({ responseToPost: body });
  };
  render() {
    return (
      <div className="App">
        <div className="container">
          <p className="text">
            Dabar per <img src={logo} alt="Logo" /> rodome:
          </p>
          <p>{this.state.response}</p>
          <form onSubmit={this.handleSubmit}>
            <p>
              <strong>Post to Server:</strong>
            </p>

            <div className="input-group mb-3">
              <input
                type="text"
                className="form-control"
                aria-describedby="button-addon"
                value={this.state.post}
                onChange={e => this.setState({ post: e.target.value })}
              />
              <div className="input-group-append">
                <button
                  className="btn btn-primary"
                  type="submit"
                  id="button-addon"
                >
                  Submit
                </button>
              </div>
            </div>
          </form>
          <p>{this.state.responseToPost}</p>
        </div>
      </div>
    );
  }
}
export default App;
