import React, { Component } from "react";
import "./App.css";
import logo from "./logo.jpg";
class App extends Component {
  state = {
    response: ""
  };

  componentDidMount() {
    this.callApi()
      .then(res =>
        this.setState({ response: [res.title, res.desc, res.start, res.end] })
      )
      .catch(err => console.log(err));
  }
  callApi = async () => {
    const response = await fetch(`/api/get`);
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };
  render() {
    return (
      <div className="App">
        <div className="container">
          <p className="text">
            Dabar per <img src={logo} alt="Logo" /> rodome:
          </p>
          <div class="row smaller">
            <div className="col-sm-12">Laikas</div>
          </div>
          <div className="row big">
            <div className="col-sm-12">{this.state.response[2]} - {this.state.response[3]}</div>
          </div>
          <div className="row smaller">
            <div className="col-sm-12">Pavadinimas</div>
          </div>
          <div className="row big">
            <div className="col-sm-12">{this.state.response[0]}</div>
          </div>
          <div className="row smaller">
            <div className="col-sm-12">Apra≈°ymas</div>
          </div>
          <div className="row big">
            <div className="col-sm-12">{this.state.response[1]}</div>
          </div>
          <a className="link" href="https://www.lrt.lt/mediateka/tiesiogiai/lrt-televizija" target="_blank">www.lrt.lt</a>
        </div>
      </div>
    );
  }
}
export default App;

